import React, {Component} from 'react'

import './App.css'

const list = [
    {
        project:'Ladokolla',
    pelatis:'Lavranos',
     objectID:'0'
    },
    {
        project:'giannades',
        pelatis:'Kantunis',
        objectID:'1'
    },
    {
        project:'sidari',
        pelatis:'sidariotis',
        objectID:'2'
    }

]


class FrankComponent extends Component {



                       state = {
                        list,
                        }



                      onDismiss(x) {
                        const isNotId = y => y.objectID !== x;
                        const updatedList = this.state.list.filter(isNotId);
                        this.setState({ list: updatedList });
                        }



    render () {
        return(
        <div>{this.state.list.map(x=><div key={x.objectID}>{x.project+' '+x.pelatis}<span><button onClick={() => this.onDismiss(x.objectID)}>REMOVE THIS</button></span></div>)}</div>   //dont forget the key (IMPORTANT)
        )
    }


}


export default FrankComponent
