import React from 'react'

import './App.css'

const list = [
    {
    project:'Ladokolla',
    pelatis:'Lavranos',
    objectID:'0',
    madeUsing:'Wordpress'
    },
    {
        project:'giannades',
        pelatis:'Kantunis',
        objectID:'1',
        madeUsing:'HTML,CSS,JS'
    },
    {
        project:'sidari',
        pelatis:'sidariotis',
        objectID:'2',
        madeUsing:'HTML,CSS,JS'
    }

]

const isSearched = (searchTerm) => (item) =>!searchTerm || item.pelatis.toLowerCase().includes(searchTerm.toLowerCase()) || item.project.toLowerCase().includes(searchTerm.toLowerCase());



class App extends React.Component {

    constructor(props) {
                        super(props);
                        this.state = {
                        list,searchTerm:'',
                        };
                        this.onDismiss = this.onDismiss.bind(this);
                        this.onSearchChange = this.onSearchChange.bind(this);
                        }

                      onDismiss(id) {
                        const isNotId = y => y.objectID !== id;
                        const updatedList = this.state.list.filter(isNotId);
                        this.setState({ list: updatedList });
                        }
                        onSearchChange (event) {
                            this.setState ({searchTerm:event.target.value}); 
                        }


    render () {
        const { searchTerm, list } = this.state;
        return(
        <div><form><input type='text' value={searchTerm} onChange={this.onSearchChange} placeholder='Search'/></form>{list.filter(isSearched(searchTerm)).map(x=><div key={x.objectID}>{x.project+' '+x.pelatis+' '+x.madeUsing}<span><button onClick={() => this.onDismiss(x.objectID)}>REMOVE THIS</button></span></div>)}
        </div>   //dont forget the key (IMPORTANT)
        )
    }


}


export default App
