import React from 'react'

import './App.css'

const list = [
    {
    project:'Ladokolla',
    pelatis:'Lavranos',
    objectID:'0',
    madeUsing:'Wordpress',
    type:'while working in company',
    url:'http://ladokollacorfu.gr/el/',
    desc:'Whole project build using wordpress'

    },
    {
        project:'giannades',
        pelatis:'Kantunis',
        objectID:'1',
        madeUsing:'HTML,CSS,JS',
        type:'freelance',
        url:'http://ladokollacorfu.gr/el/',
        desc:'Whole project with design and build used three.js library for the birds in the intro'
    },
    {
        project:'sidari',
        pelatis:'sidariotis',
        objectID:'2',
        madeUsing:'HTML,CSS,JS',
        type:'while working in a company',
        url:'http://ladokollacorfu.gr/el/',
        desc:'Freelance project everything except design'

    },
    {
        project:'funwithflags',
        pelatis:'personal',
        objectID:'3',
        madeUsing:'React',
        type:'personal',
        url:'http://funwithflags.frankagathos.com/'
    },


]



const isSearched = (searchTerm) => (item) =>!searchTerm || item.pelatis.toLowerCase().includes(searchTerm.toLowerCase()) || item.project.toLowerCase().includes(searchTerm.toLowerCase()) || item.madeUsing.toLowerCase().includes(searchTerm.toLowerCase()) || item.url.toLowerCase().includes(searchTerm.toLowerCase());

class App extends React.Component {

    constructor(props) {
                        super(props);
                        this.state = {
                        list,searchTerm:'',
                            putsa:0

                        };
                        this.onDismiss = this.onDismiss.bind(this);//bind the method to the state
                        this.onSearchChange = this.onSearchChange.bind(this);//bind the method to the state

                        }

                      onDismiss(id) {
                        const isNotId = y => y.objectID !== id; //if the evaluation for an item is true, the item stays in the list
                        const updatedList = this.state.list.filter(isNotId); //the filter function takes a function to evaluate each item in the list
                        this.setState({ list: updatedList }); // update the state of the list
                        }

                        onSearchChange (event) {
                            this.setState ({searchTerm:event.target.value}); //The event has the value of the input field in its object.
                        }

                      searchWordpress = ()=> {
                       this.setState ({searchTerm:'wordpress'});
                        }

                      searchReact = ()=> {
                       this.setState ({searchTerm:'react'});
                        }

    render () {
        const { searchTerm, list } = this.state; //destructuring
        return(
        <div className="App">
        <Search
        value={searchTerm}
        onChange={this.onSearchChange}
        >
        Search my work
        </Search> 

        <span><Button onClick={this.searchWordpress} text="Wordpress"/><Button onClick={this.searchReact} text="React"/></span>
        <Table list={list} pattern={searchTerm} onDismiss={this.onDismiss} />


        </div>   //dont forget the key (IMPORTANT)
        )
    }      
}



//functional stateless component Search
class Search extends React.Component {
_handleKeyPress = (x)=> {
                         if (x.key === 'Enter') {
       x.preventDefault();
                              onSubmit();
    }
}
   onSubmit=(e) => {
        /**
         * Prevent submit from reloading the page
         */
        e.preventDefault();
        e.stopPropagation();
        onSubmit();
      }

    render () {
      const {value,onChange,children}  = this.props; //destructuring  
        return(
                     <form> 
         {children}<input type='text' value={value} onChange={onChange} onKeyPress={this._handleKeyPress}  placeholder='Search projects'/> 
      </form>
        )

    }


}



//functional stateless component Table
const Table = ({list,pattern,onDismiss}) => 
            <div>
                {list.filter(isSearched(pattern)).map(x=><div className='project-card' key={x.objectID}>{x.project+' '+x.pelatis+' '+x.madeUsing+' '}
                <span><Button text="REMOVE" onClick={() => onDismiss(x.objectID)}></Button></span> <span><button><a href={x.url} target="_blank" rel="noopener noreferrer">VISIT SITE</a></button></span> <div>{x.desc}</div>
                </div>)}
            </div>



//functional stateless component Button
const Button = ({onClick,className='',children,text=''}) =>  
            <button 
              onClick={onClick}
              className={className}
              type="button"
            >{text}
            </button>


export default App
