import React from 'react'

import './App.css'

const list = [
    {
    project:'Ladokolla',
    pelatis:'Lavranos',
    objectID:'0',
    madeUsing:'Wordpress'
    },
    {
        project:'giannades',
        pelatis:'Kantunis',
        objectID:'1',
        madeUsing:'HTML,CSS,JS'
    },
    {
        project:'sidari',
        pelatis:'sidariotis',
        objectID:'2',
        madeUsing:'HTML,CSS,JS'
    }

]



const isSearched = (searchTerm) => (item) =>!searchTerm || item.pelatis.toLowerCase().includes(searchTerm.toLowerCase()) || item.project.toLowerCase().includes(searchTerm.toLowerCase()) || item.madeUsing.toLowerCase().includes(searchTerm.toLowerCase());

class App extends React.Component {

    constructor(props) {
                        super(props);
                        this.state = {
                        list,searchTerm:'',
                        };
                        this.onDismiss = this.onDismiss.bind(this);//bind the method to the state
                        this.onSearchChange = this.onSearchChange.bind(this);//bind the method to the state
                        }

                      onDismiss(id) {
                        const isNotId = y => y.objectID !== id; //if the evaluation for an item is true, the item stays in the list
                        const updatedList = this.state.list.filter(isNotId); //the filter function takes a function to evaluate each item in the list
                        this.setState({ list: updatedList }); // update the state of the list
                        }

                        onSearchChange (event) {
                            this.setState ({searchTerm:event.target.value}); //The event has the value of the input field in its object.
                        }


    render () {
        const { searchTerm, list } = this.state; //destructuring
        return(
        <div className="page">
            <div className="interactions">
                <Search
                value={searchTerm}
                onChange={this.onSearchChange}
                >
                Search
                </Search>
                </div>
        <Table list={list} pattern={searchTerm} onDismiss={this.onDismiss} />

        </div>   //dont forget the key (IMPORTANT)
        )
    }      
}



//functional stateless component Search
const Search = ({value,onChange,children}) => 
    <form> 
         {children}<input type='text' value={value} onChange={onChange} placeholder='Search anything'/> 
      </form>



//functional stateless component Table
const Table = ({list,pattern,onDismiss}) => 
           <div className="table">
                {list.filter(isSearched(pattern)).map(x=><div key={x.objectID} className="table-row">{x.project+' '+x.pelatis+' '+x.madeUsing+' '}
                <span><Button className="button-inline"onClick={() => onDismiss(x.objectID)}>REMOVE THIS</Button></span> </div>)}
            </div>



//functional stateless component Button
const Button = ({onClick,className='',children}) =>       <button 
              onClick={onClick}
              className={className}
              type="button"

            >{children}
            </button>


export default App
