import React from 'react'

import './App.css'

const list = [
    {
        project:'Ladokolla',
    pelatis:'Lavranos',
     objectID:'0'
    },
    {
        project:'giannades',
        pelatis:'Kantunis',
        objectID:'1'
    },
    {
        project:'sidari',
        pelatis:'sidariotis',
        objectID:'2'
    }

]


class FrankComponent extends React.Component {

    constructor(props) {
                        super(props);
                        this.state = {
                        list,
                        };
                        this.onDismiss = this.onDismiss.bind(this);
                        this.onSearchChange = this.onSearchChange.bind(this);
                        }

                      onDismiss(id) {
                        const isNotId = y => y.objectID !== id;
                        const updatedList = this.state.list.filter(isNotId);
                        this.setState({ list: updatedList });
                        }
                        onSearchChange (event) {
                            this.setState ({searchTerm:event.target.value}); 
                        }


    render () {
        return(
        <div><form><input type='text' onChange={this.onSearchChange} placeholder='Search for a client'/></form>{this.state.list.map(x=><div key={x.objectID}>{x.project+' '+x.pelatis}<span><button onClick={() => this.onDismiss(x.objectID)}>REMOVE THIS</button></span></div>)}</div>   //dont forget the key (IMPORTANT)
        )
    }


}


export default FrankComponent
